import React, { useState } from 'react';
import './Reports.css';

const ReportsSection = ({ 
  reports, 
  loading, 
  user, 
  setShowAddForm, 
  fetchReports, 
  downloadExcel, 
  handleEditReport, 
  handleDeleteReport, 
  canCreateReport, 
  canViewAllReports, 
  canEditDelete 
}) => {
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');

  const handleAddFeedback = (report) => {
    setSelectedReport(report);
    setFeedback(report.feedback || '');
    setShowFeedbackModal(true);
  };

  const handleSubmitFeedback = () => {
    if (selectedReport && feedback.trim()) {
      // Here you would typically make an API call to save the feedback
      console.log('Submitting feedback for report:', selectedReport.id, feedback);

      // Simulate API call success
      alert(`Feedback submitted for ${selectedReport.coursename} report!`);
      setShowFeedbackModal(false);
      setFeedback('');
      setSelectedReport(null);
    }
  };

  const canAddFeedback = (report) => {
    return user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER';
  };

  return (
    <>
      <div className="card p-3">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0">
            {user?.role === 'STUDENT' ? 'Available Reports' : 'All Class Reports'}
            {user?.role === 'LECTURER' && ' (My Reports)'}
            {(user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER') && ' (With Feedback)'}
          </h5>
          <div>
            {canCreateReport && (
              <button 
                className="btn btn-success btn-sm me-2"
                onClick={() => setShowAddForm(true)}
              >
                + Add New Report
              </button>
            )}
            {canViewAllReports && (
              <button 
                className="btn btn-excel btn-sm me-2"
                onClick={downloadExcel}
                disabled={reports.length === 0}
              >
                <i className="fas fa-file-excel me-1"></i>Export All
              </button>
            )}
            <button 
              className="btn btn-primary btn-sm"
              onClick={fetchReports}
              disabled={loading}
            >
              {loading ? 'Refreshing...' : 'Refresh'}
            </button>
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-striped">
            <thead>
              <tr>
                <th>#</th>
                <th>Course</th>
                <th>Code</th>
                <th>Lecturer</th>
                <th>Class</th>
                <th>Attendance</th>
                <th>Week</th>
                <th>Date</th>
                {(user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER') && (
                  <th>Feedback</th>
                )}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan={user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER' ? "10" : "9"} className="text-center">
                    <div className="spinner-border spinner-border-sm me-2"></div>
                    Loading reports...
                  </td>
                </tr>
              ) : reports.length === 0 ? (
                <tr>
                  <td colSpan={user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER' ? "10" : "9"} className="text-center text-muted py-4">
                    <div>No reports found.</div>
                    <small>
                      {user?.role === 'STUDENT' 
                        ? 'No reports available for viewing.' 
                        : 'Click "Add New Report" to create your first report'
                      }
                    </small>
                  </td>
                </tr>
              ) : (
                reports.map((report, index) => (
                  <tr key={report.id}>
                    <td>{index + 1}</td>
                    <td>{report.coursename || 'N/A'}</td>
                    <td>{report.coursecode || 'N/A'}</td>
                    <td>{report.lecturer || 'N/A'}</td>
                    <td>{report.class_name || 'N/A'}</td>
                    <td>
                      <span className="badge bg-info">
                        {report.present || 0}/{report.registered || 0}
                      </span>
                    </td>
                    <td>{report.week_of_reporting || 'N/A'}</td>
                    <td>
                      {report.date_of_lecture ? 
                        new Date(report.date_of_lecture).toLocaleDateString() : 'N/A'
                      }
                    </td>
                    {(user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER') && (
                      <td>
                        {report.feedback ? (
                          <span className="badge bg-success" title={report.feedback}>
                            <i className="fas fa-check me-1"></i>Provided
                          </span>
                        ) : (
                          <span className="badge bg-warning">Pending</span>
                        )}
                      </td>
                    )}
                    <td>
                      <div className="btn-group" role="group">
                        {canEditDelete(report) && (
                          <>
                            <button 
                              className="btn btn-warning btn-sm me-1"
                              onClick={() => handleEditReport(report)}
                              title="Edit Report"
                            >
                              <i className="fas fa-edit"></i>
                            </button>
                            <button 
                              className="btn btn-danger btn-sm me-1"
                              onClick={() => handleDeleteReport(report.id, report)}
                              title="Delete Report"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </>
                        )}
                        {canAddFeedback(report) && (
                          <button 
                            className="btn btn-secondary btn-sm me-1"
                            onClick={() => handleAddFeedback(report)}
                            title="Add Feedback"
                          >
                            <i className="fas fa-comment"></i>
                          </button>
                        )}
                        <button 
                          className="btn btn-info btn-sm"
                          onClick={() => {
                            alert(`Report Details:\nCourse: ${report.coursename}\nLecturer: ${report.lecturer}\nTopic: ${report.topic_taught}`);
                          }}
                          title="View Details"
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Feedback Modal */}
      {showFeedbackModal && (
        <div className="modal fade show" style={{display: 'block', backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Add Feedback</h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setShowFeedbackModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                {selectedReport && (
                  <div className="mb-3">
                    <p><strong>Course:</strong> {selectedReport.coursename}</p>
                    <p><strong>Lecturer:</strong> {selectedReport.lecturer}</p>
                    <p><strong>Class:</strong> {selectedReport.class_name}</p>
                    <p><strong>Week:</strong> {selectedReport.week_of_reporting}</p>
                  </div>
                )}
                <div className="mb-3">
                  <label htmlFor="feedbackText" className="form-label">Your Feedback:</label>
                  <textarea 
                    className="form-control" 
                    id="feedbackText" 
                    rows="4"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    placeholder="Enter your feedback for this report..."
                  ></textarea>
                </div>
              </div>
              <div className="modal-footer">
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => setShowFeedbackModal(false)}
                >
                  Cancel
                </button>
                <button 
                  type="button" 
                  className="btn btn-primary" 
                  onClick={handleSubmitFeedback}
                  disabled={!feedback.trim()}
                >
                  Submit Feedback
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ReportsSection;
