import React from 'react';
import './PRL.css';

const ProgramLeaderSidebar = ({ activeSection, setActiveSection }) => {
  const menuItems = [
    { id: 'dashboard', icon: 'fas fa-tachometer-alt', label: 'Dashboard' },
    { id: 'faculty_management', icon: 'fas fa-user-tie', label: 'Faculty Management' },
    { id: 'academic_monitoring', icon: 'fas fa-chart-line', label: 'Academic Monitoring' },
    { id: 'performance_rating', icon: 'fas fa-star', label: 'Performance Rating' },
    { id: 'course_oversight', icon: 'fas fa-book-open', label: 'Course Oversight' },
    { id: 'reports_analytics', icon: 'fas fa-chart-pie', label: 'Reports & Analytics' },
    { id: 'quality_assurance', icon: 'fas fa-clipboard-check', label: 'Quality Assurance' },
    { id: 'faculty_development', icon: 'fas fa-graduation-cap', label: 'Faculty Development' },
    { id: 'settings', icon: 'fas fa-cogs', label: 'System Settings' }
  ];

  return (
    <div className="sidebar text-white">
      {/* Updated Header - PRL instead of STUDENT */}
      <div className="sidebar-header px-3 py-4 text-center">
        <div className="principal-badge mb-2">
          <i className="fas fa-crown fa-2x text-warning"></i>
        </div>
        <h5 className="mb-1">LUCT - PRL</h5>
        <small className="text-muted">Principal Lecturer Dashboard</small>
      </div>

      <nav className="sidebar-nav mt-3">
        {menuItems.map((item) => (
          <a
            key={item.id}
            href="#"
            className={`sidebar-link ${activeSection === item.id ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              setActiveSection(item.id);
            }}
          >
            <i className={`${item.icon} me-3`}></i>
            {item.label}
            {item.id === 'faculty_management' && <span className="badge bg-warning ms-2">Admin</span>}
          </a>
        ))}
      </nav>

      <div className="sidebar-footer px-3 py-3 text-center">
        <small className="text-muted">
          <i className="fas fa-shield-alt me-1"></i>
          Academic Leadership Access
        </small>
      </div>
    </div>
  );
};

export default ProgramLeaderSidebar;
