import React, { useState } from 'react';
import './Reports.css';

const ReportsSection = ({ 
  reports, 
  loading, 
  user, 
  setShowAddForm, 
  fetchReports, 
  downloadExcel, 
  handleEditReport, 
  handleDeleteReport, 
  canCreateReport, 
  canViewAllReports, 
  canEditDelete 
}) => {
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');

  const handleAddFeedback = (report) => {
    setSelectedReport(report);
    setFeedback(report.feedback || '');
    setShowFeedbackModal(true);
  };

  const handleSubmitFeedback = () => {
    if (selectedReport && feedback.trim()) {
      // You can later replace this with an API call
      console.log('Submitting feedback for report:', selectedReport.id, feedback);
      alert(`Feedback submitted for ${selectedReport.coursename}!\n\nFeedback: ${feedback}`);
      setShowFeedbackModal(false);
      setFeedback('');
      setSelectedReport(null);
      fetchReports(); // refresh reports
    }
  };

  // ðŸ”§ Simplified permission logic:
  const canAddFeedback = (report) => user?.role === 'PRINCIPAL_LECTURER';
  const canReviewFeedback = (report) => user?.role === 'PROGRAM_LEADER';
  const canViewFeedback = () =>
    user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER';

  // Filter reports by user role
  const getFilteredReports = () => {
    if (!user || !reports) return reports;
    switch(user.role) {
      case 'PRINCIPAL_LECTURER':
        return reports; // show all under their stream (placeholder)
      case 'PROGRAM_LEADER':
        return reports; // show all with PRL feedback (placeholder)
      case 'LECTURER':
        return reports.filter(r => r.lecturer === user.name);
      default:
        return reports;
    }
  };

  const filteredReports = getFilteredReports();

  const getHeaderTitle = () => {
    switch(user?.role) {
      case 'PRINCIPAL_LECTURER': return 'Stream Reports - Add Feedback';
      case 'PROGRAM_LEADER': return 'PRL Reports Review';
      case 'LECTURER': return 'My Reports';
      default: return 'Reports Overview';
    }
  };

  return (
    <>
      <div className="card p-3">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0">{getHeaderTitle()}</h5>
          <div>
            {canCreateReport && (
              <button className="btn btn-success btn-sm me-2" onClick={() => setShowAddForm(true)}>
                + Add New Report
              </button>
            )}
            {canViewAllReports && (
              <button 
                className="btn btn-excel btn-sm me-2"
                onClick={downloadExcel}
                disabled={filteredReports.length === 0}
              >
                <i className="fas fa-file-excel me-1"></i>Export All
              </button>
            )}
            <button 
              className="btn btn-primary btn-sm"
              onClick={fetchReports}
              disabled={loading}
            >
              {loading ? 'Refreshing...' : 'Refresh'}
            </button>
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-striped">
            <thead>
              <tr>
                <th>#</th>
                <th>Course</th>
                <th>Code</th>
                <th>Lecturer</th>
                <th>Class</th>
                <th>Attendance</th>
                <th>Week</th>
                <th>Date</th>
                {canViewFeedback() && <th>Feedback</th>}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan="10" className="text-center">Loading reports...</td>
                </tr>
              ) : filteredReports.length === 0 ? (
                <tr>
                  <td colSpan="10" className="text-center text-muted py-4">No reports found.</td>
                </tr>
              ) : (
                filteredReports.map((report, index) => (
                  <tr key={report.id}>
                    <td>{index + 1}</td>
                    <td>{report.coursename}</td>
                    <td>{report.coursecode}</td>
                    <td>{report.lecturer}</td>
                    <td>{report.class_name}</td>
                    <td><span className="badge bg-info">{report.present || 0}/{report.registered || 0}</span></td>
                    <td>{report.week_of_reporting}</td>
                    <td>{report.date_of_lecture ? new Date(report.date_of_lecture).toLocaleDateString() : 'N/A'}</td>
                    {canViewFeedback() && (
                      <td>
                        {report.feedback ? (
                          <span className="badge bg-success">Provided</span>
                        ) : (
                          <span className="badge bg-secondary">None</span>
                        )}
                      </td>
                    )}
                    <td>
                      <div className="btn-group" role="group">
                        {canEditDelete(report) && (
                          <>
                            <button 
                              className="btn btn-warning btn-sm me-1"
                              onClick={() => handleEditReport(report)}
                              title="Edit Report"
                            >
                              <i className="fas fa-edit"></i>
                            </button>
                            <button 
                              className="btn btn-danger btn-sm me-1"
                              onClick={() => handleDeleteReport(report.id, report)}
                              title="Delete Report"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </>
                        )}

                        {/* ðŸ”§ Feedback button for PRL and PL */}
                        {(canAddFeedback(report) || canReviewFeedback(report)) && (
                          <button
                            className={`btn btn-sm me-1 ${
                              report.feedback ? 'btn-outline-info' : 'btn-info'
                            }`}
                            onClick={() => handleAddFeedback(report)}
                            title={report.feedback ? 'Edit Feedback' : 'Add Feedback'}
                          >
                            <i className="fas fa-comment-dots"></i>
                            {report.feedback && <i className="fas fa-check ms-1"></i>}
                          </button>
                        )}

                        <button
                          className="btn btn-primary btn-sm"
                          onClick={() => {
                            const details = `
Course: ${report.coursename}
Lecturer: ${report.lecturer}
Week: ${report.week_of_reporting}
Feedback: ${report.feedback || 'None'}
                            `;
                            alert(details);
                          }}
                          title="View Details"
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Feedback Modal */}
      {showFeedbackModal && (
        <div className="modal fade show" style={{display: 'block', backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">
                  <i className="fas fa-comment-dots me-2"></i>
                  {user?.role === 'PRINCIPAL_LECTURER' ? 'Add Teaching Feedback' : 'Review Feedback'}
                </h5>
                <button type="button" className="btn-close btn-close-white" onClick={() => setShowFeedbackModal(false)}></button>
              </div>
              <div className="modal-body">
                <textarea
                  className="form-control"
                  rows="6"
                  value={feedback}
                  onChange={(e) => setFeedback(e.target.value)}
                  placeholder="Type feedback or review notes here..."
                ></textarea>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setShowFeedbackModal(false)}>Cancel</button>
                <button className="btn btn-success" onClick={handleSubmitFeedback} disabled={!feedback.trim()}>
                  <i className="fas fa-check me-1"></i>Save Feedback
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ReportsSection;
