import React, { useState } from 'react';
import './Reports.css';

const ReportsSection = ({ 
  reports, 
  loading, 
  user, 
  setShowAddForm, 
  fetchReports, 
  downloadExcel, 
  handleEditReport, 
  handleDeleteReport, 
  canCreateReport, 
  canViewAllReports, 
  canEditDelete,
  handleSubmitFeedback // NEW: Added this prop
}) => {
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');

  const handleAddFeedback = (report) => {
    setSelectedReport(report);
    setFeedback(report.feedback || '');
    setShowFeedbackModal(true);
  };

  const handleSubmitFeedbackAction = async () => {
    if (selectedReport && feedback.trim()) {
      if (handleSubmitFeedback) {
        // Use the API handler from Dashboard
        const success = await handleSubmitFeedback(selectedReport.id, feedback);
        if (success) {
          setShowFeedbackModal(false);
          setFeedback('');
          setSelectedReport(null);
        }
      } else {
        // Fallback: local handling
        console.log('Submitting feedback for report:', selectedReport.id, feedback);
        alert(`Feedback submitted for ${selectedReport.coursename}!\n\nFeedback: ${feedback}`);
        setShowFeedbackModal(false);
        setFeedback('');
        setSelectedReport(null);
        fetchReports();
      }
    }
  };

  // ðŸ”§ FIXED: Use lowercase roles to match Dashboard
  const canAddFeedback = () => user?.role === 'principal_lecturer' || user?.role === 'program_leader';
  const canViewFeedback = () => user?.role === 'principal_lecturer' || user?.role === 'program_leader';

  // Filter reports by user role
  const getFilteredReports = () => {
    if (!user || !reports) return reports;
    switch(user.role) {
      case 'principal_lecturer':
        return reports; // PRL sees all reports in their stream
      case 'program_leader':
        return reports; // PL sees all reports
      case 'lecturer':
        return reports.filter(r => r.lecturer === user.name);
      case 'student':
        return reports.filter(r => r.class_name === user.className); // Adjust based on student data
      default:
        return reports;
    }
  };

  const filteredReports = getFilteredReports();

  const getHeaderTitle = () => {
    switch(user?.role) {
      case 'principal_lecturer': return 'Stream Reports - Add Feedback';
      case 'program_leader': return 'All Reports - Review Feedback';
      case 'lecturer': return 'My Reports';
      case 'student': return 'Available Reports';
      default: return 'Reports Overview';
    }
  };

  return (
    <>
      <div className="card p-3">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0">{getHeaderTitle()}</h5>
          <div>
            {canCreateReport && (
              <button className="btn btn-success btn-sm me-2" onClick={() => setShowAddForm(true)}>
                + Add New Report
              </button>
            )}
            {canViewAllReports && (
              <button 
                className="btn btn-excel btn-sm me-2"
                onClick={downloadExcel}
                disabled={filteredReports.length === 0}
              >
                <i className="fas fa-file-excel me-1"></i>Export All
              </button>
            )}
            <button 
              className="btn btn-primary btn-sm"
              onClick={fetchReports}
              disabled={loading}
            >
              {loading ? 'Refreshing...' : 'Refresh'}
            </button>
          </div>
        </div>

        {/* Role-specific instructions */}
        {user?.role === 'principal_lecturer' && (
          <div className="alert alert-info mb-3">
            <i className="fas fa-info-circle me-2"></i>
            <strong>Principal Lecturer Access:</strong> You can add feedback to any report in your stream.
            Look for the <i className="fas fa-comment-dots text-info mx-1"></i> button in the Actions column.
          </div>
        )}

        <div className="table-responsive">
          <table className="table table-dark table-striped">
            <thead>
              <tr>
                <th>#</th>
                <th>Course</th>
                <th>Code</th>
                <th>Lecturer</th>
                <th>Class</th>
                <th>Attendance</th>
                <th>Week</th>
                <th>Date</th>
                {canViewFeedback() && <th>Feedback Status</th>}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan={canViewFeedback() ? "10" : "9"} className="text-center">
                    <div className="spinner-border spinner-border-sm me-2"></div>
                    Loading reports...
                  </td>
                </tr>
              ) : filteredReports.length === 0 ? (
                <tr>
                  <td colSpan={canViewFeedback() ? "10" : "9"} className="text-center text-muted py-4">
                    No reports found.
                  </td>
                </tr>
              ) : (
                filteredReports.map((report, index) => (
                  <tr key={report.id || index}>
                    <td>{index + 1}</td>
                    <td>{report.coursename || 'N/A'}</td>
                    <td>{report.coursecode || 'N/A'}</td>
                    <td>{report.lecturer || 'N/A'}</td>
                    <td>{report.class_name || 'N/A'}</td>
                    <td>
                      <span className="badge bg-info">
                        {report.present || 0}/{report.registered || 0}
                      </span>
                    </td>
                    <td>{report.week_of_reporting || 'N/A'}</td>
                    <td>
                      {report.date_of_lecture ? 
                        new Date(report.date_of_lecture).toLocaleDateString() : 'N/A'
                      }
                    </td>
                    {canViewFeedback() && (
                      <td>
                        {report.feedback ? (
                          <span className="badge bg-success" title={report.feedback}>
                            <i className="fas fa-check me-1"></i>Provided
                          </span>
                        ) : (
                          <span className="badge bg-warning">Pending</span>
                        )}
                      </td>
                    )}
                    <td>
                      <div className="btn-group" role="group">
                        {canEditDelete(report) && (
                          <>
                            <button 
                              className="btn btn-warning btn-sm me-1"
                              onClick={() => handleEditReport(report)}
                              title="Edit Report"
                            >
                              <i className="fas fa-edit"></i>
                            </button>
                            <button 
                              className="btn btn-danger btn-sm me-1"
                              onClick={() => handleDeleteReport(report.id, report)}
                              title="Delete Report"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </>
                        )}

                        {/* ðŸ”§ FIXED: Feedback button for PRL and PL */}
                        {canAddFeedback() && (
                          <button
                            className={`btn btn-sm me-1 ${
                              report.feedback ? 'btn-outline-info' : 'btn-info'
                            }`}
                            onClick={() => handleAddFeedback(report)}
                            title={report.feedback ? 'View/Edit Feedback' : 'Add Feedback'}
                          >
                            <i className="fas fa-comment-dots"></i>
                            {report.feedback && <span className="ms-1">âœ“</span>}
                          </button>
                        )}

                        <button
                          className="btn btn-primary btn-sm"
                          onClick={() => {
                            const details = `
Report Details:
â€¢ Course: ${report.coursename}
â€¢ Lecturer: ${report.lecturer}
â€¢ Class: ${report.class_name}
â€¢ Week: ${report.week_of_reporting}
â€¢ Attendance: ${report.present || 0}/${report.registered || 0}
â€¢ Topic: ${report.topic_taught || 'N/A'}
${report.feedback ? `â€¢ Feedback: ${report.feedback}` : 'â€¢ Feedback: Not provided yet'}
                            `;
                            alert(details);
                          }}
                          title="View Details"
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Feedback Modal */}
      {showFeedbackModal && (
        <div className="modal fade show" style={{display: 'block', backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">
                  <i className="fas fa-comment-dots me-2"></i>
                  {selectedReport?.feedback ? 'Edit Feedback' : 'Add Feedback'} for Report
                </h5>
                <button type="button" className="btn-close btn-close-white" onClick={() => setShowFeedbackModal(false)}></button>
              </div>
              <div className="modal-body">
                {selectedReport && (
                  <div className="mb-3 p-3 bg-dark rounded">
                    <h6>Report Information:</h6>
                    <div className="row">
                      <div className="col-md-6">
                        <strong>Course:</strong> {selectedReport.coursename}<br/>
                        <strong>Code:</strong> {selectedReport.coursecode}<br/>
                        <strong>Lecturer:</strong> {selectedReport.lecturer}
                      </div>
                      <div className="col-md-6">
                        <strong>Class:</strong> {selectedReport.class_name}<br/>
                        <strong>Week:</strong> {selectedReport.week_of_reporting}<br/>
                        <strong>Date:</strong> {selectedReport.date_of_lecture ? new Date(selectedReport.date_of_lecture).toLocaleDateString() : 'N/A'}
                      </div>
                    </div>
                  </div>
                )}
                <div className="mb-3">
                  <label className="form-label fw-bold">
                    {user?.role === 'principal_lecturer' ? 'Your Teaching Feedback:' : 'Review Comments:'}
                  </label>
                  <textarea
                    className="form-control"
                    rows="6"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    placeholder={
                      user?.role === 'principal_lecturer' 
                        ? "Provide constructive feedback on teaching methods, student engagement, areas for improvement..."
                        : "Add your review comments or observations..."
                    }
                    style={{ resize: 'vertical' }}
                  ></textarea>
                </div>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setShowFeedbackModal(false)}>
                  <i className="fas fa-times me-1"></i>Cancel
                </button>
                <button 
                  className="btn btn-success" 
                  onClick={handleSubmitFeedbackAction} 
                  disabled={!feedback.trim()}
                >
                  <i className="fas fa-check me-1"></i>feedback
                  {selectedReport?.feedback ? 'Update Feedback' : 'Submit Feedback'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ReportsSection;
