import React, { useState } from 'react';
import './Reports.css';

const ReportsSection = ({ 
  reports, 
  loading, 
  user, 
  setShowAddForm, 
  fetchReports, 
  downloadExcel, 
  handleEditReport, 
  handleDeleteReport, 
  canCreateReport, 
  canViewAllReports, 
  canEditDelete 
}) => {
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');

  const handleAddFeedback = (report) => {
    setSelectedReport(report);
    setFeedback(report.feedback || '');
    setShowFeedbackModal(true);
  };

  const handleSubmitFeedback = () => {
    if (selectedReport && feedback.trim()) {
      // Here you would typically make an API call to save the feedback
      console.log('Submitting feedback for report:', selectedReport.id, feedback);

      // Simulate API call success - update the report with feedback
      const updatedReports = reports.map(report => 
        report.id === selectedReport.id 
          ? { ...report, feedback: feedback }
          : report
      );

      // In a real app, you would update state via props or context
      // For now, we'll show an alert
      alert(`Feedback submitted for ${selectedReport.coursename} report!\n\nFeedback: ${feedback}`);

      setShowFeedbackModal(false);
      setFeedback('');
      setSelectedReport(null);

      // Refresh the reports to show updated feedback
      fetchReports();
    }
  };

  const canAddFeedback = () => {
    return user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER';
  };

  return (
    <>
      <div className="card p-3">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0">
            {user?.role === 'STUDENT' ? 'Available Reports' : 'All Class Reports'}
            {user?.role === 'LECTURER' && ' (My Reports)'}
            {(user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER') && ' (With Feedback)'}
          </h5>
          <div>
            {canCreateReport && (
              <button 
                className="btn btn-success btn-sm me-2"
                onClick={() => setShowAddForm(true)}
              >
                + Add New Report
              </button>
            )}
            {canViewAllReports && (
              <button 
                className="btn btn-excel btn-sm me-2"
                onClick={downloadExcel}
                disabled={reports.length === 0}
              >
                <i className="fas fa-file-excel me-1"></i>Export All
              </button>
            )}
            <button 
              className="btn btn-primary btn-sm"
              onClick={fetchReports}
              disabled={loading}
            >
              {loading ? 'Refreshing...' : 'Refresh'}
            </button>
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-striped">
            <thead>
              <tr>
                <th>#</th>
                <th>Course</th>
                <th>Code</th>
                <th>Lecturer</th>
                <th>Class</th>
                <th>Attendance</th>
                <th>Week</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan="9" className="text-center">
                    <div className="spinner-border spinner-border-sm me-2"></div>
                    Loading reports...
                  </td>
                </tr>
              ) : reports.length === 0 ? (
                <tr>
                  <td colSpan="9" className="text-center text-muted py-4">
                    <div>No reports found.</div>
                    <small>
                      {user?.role === 'STUDENT' 
                        ? 'No reports available for viewing.' 
                        : 'Click "Add New Report" to create your first report'
                      }
                    </small>
                  </td>
                </tr>
              ) : (
                reports.map((report, index) => (
                  <tr key={report.id}>
                    <td>{index + 1}</td>
                    <td>{report.coursename || 'N/A'}</td>
                    <td>{report.coursecode || 'N/A'}</td>
                    <td>{report.lecturer || 'N/A'}</td>
                    <td>{report.class_name || 'N/A'}</td>
                    <td>
                      <span className="badge bg-info">
                        {report.present || 0}/{report.registered || 0}
                      </span>
                    </td>
                    <td>{report.week_of_reporting || 'N/A'}</td>
                    <td>
                      {report.date_of_lecture ? 
                        new Date(report.date_of_lecture).toLocaleDateString() : 'N/A'
                      }
                    </td>
                    <td>
                      <div className="btn-group" role="group">
                        {canEditDelete(report) && (
                          <>
                            <button 
                              className="btn btn-warning btn-sm me-1"
                              onClick={() => handleEditReport(report)}
                              title="Edit Report"
                            >
                              <i className="fas fa-edit"></i>
                            </button>
                            <button 
                              className="btn btn-danger btn-sm me-1"
                              onClick={() => handleDeleteReport(report.id, report)}
                              title="Delete Report"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </>
                        )}

                        {/* Feedback Button - Only for PRL and PL */}
                        {canAddFeedback() && (
                          <button 
                            className="btn btn-info btn-sm me-1"
                            onClick={() => handleAddFeedback(report)}
                            title="Add Feedback"
                          >
                            <i className="fas fa-comment-dots"></i>
                          </button>
                        )}

                        <button 
                          className="btn btn-primary btn-sm"
                          onClick={() => {
                            const details = `
Report Details:
• Course: ${report.coursename}
• Code: ${report.coursecode}
• Lecturer: ${report.lecturer}
• Class: ${report.class_name}
• Attendance: ${report.present || 0}/${report.registered || 0}
• Week: ${report.week_of_reporting}
• Date: ${report.date_of_lecture ? new Date(report.date_of_lecture).toLocaleDateString() : 'N/A'}
• Topic: ${report.topic_taught || 'N/A'}
${report.feedback ? `• Feedback: ${report.feedback}` : ''}
                            `;
                            alert(details);
                          }}
                          title="View Details"
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Feedback Modal */}
      {showFeedbackModal && (
        <div className="modal fade show" style={{display: 'block', backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">
                  <i className="fas fa-comment-dots me-2"></i>
                  Add Feedback for Report
                </h5>
                <button 
                  type="button" 
                  className="btn-close btn-close-white" 
                  onClick={() => setShowFeedbackModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                {selectedReport && (
                  <div className="row mb-4">
                    <div className="col-md-6">
                      <h6>Report Information</h6>
                      <table className="table table-sm table-bordered">
                        <tbody>
                          <tr>
                            <td><strong>Course:</strong></td>
                            <td>{selectedReport.coursename}</td>
                          </tr>
                          <tr>
                            <td><strong>Code:</strong></td>
                            <td>{selectedReport.coursecode}</td>
                          </tr>
                          <tr>
                            <td><strong>Lecturer:</strong></td>
                            <td>{selectedReport.lecturer}</td>
                          </tr>
                          <tr>
                            <td><strong>Class:</strong></td>
                            <td>{selectedReport.class_name}</td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                    <div className="col-md-6">
                      <h6>Class Details</h6>
                      <table className="table table-sm table-bordered">
                        <tbody>
                          <tr>
                            <td><strong>Attendance:</strong></td>
                            <td>
                              <span className="badge bg-info">
                                {selectedReport.present || 0}/{selectedReport.registered || 0}
                              </span>
                            </td>
                          </tr>
                          <tr>
                            <td><strong>Week:</strong></td>
                            <td>{selectedReport.week_of_reporting}</td>
                          </tr>
                          <tr>
                            <td><strong>Date:</strong></td>
                            <td>
                              {selectedReport.date_of_lecture ? 
                                new Date(selectedReport.date_of_lecture).toLocaleDateString() : 'N/A'
                              }
                            </td>
                          </tr>
                          <tr>
                            <td><strong>Topic:</strong></td>
                            <td>{selectedReport.topic_taught || 'N/A'}</td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                  </div>
                )}
                <div className="mb-3">
                  <label htmlFor="feedbackText" className="form-label fw-bold">
                    Your Feedback:
                  </label>
                  <textarea 
                    className="form-control" 
                    id="feedbackText" 
                    rows="6"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    placeholder="Enter your detailed feedback for this report. You can include comments on teaching methods, student engagement, areas for improvement, etc."
                    style={{ resize: 'vertical' }}
                  ></textarea>
                  <div className="form-text">
                    Provide constructive feedback to help improve teaching quality.
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => setShowFeedbackModal(false)}
                >
                  <i className="fas fa-times me-1"></i>Cancel
                </button>
                <button 
                  type="button" 
                  className="btn btn-success" 
                  onClick={handleSubmitFeedback}
                  disabled={!feedback.trim()}
                >
                  <i className="fas fa-check me-1"></i>Submit Feedback
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ReportsSection;
