import React, { useState } from 'react';
import './FeedbackComponent.css';

const FeedbackComponent = ({ user }) => {
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [feedbackType, setFeedbackType] = useState('lecturer'); // 'lecturer' or 'student'
  const [selectedRecipient, setSelectedRecipient] = useState('');
  const [feedbackData, setFeedbackData] = useState({
    recipientType: 'lecturer',
    recipientId: '',
    recipientName: '',
    course: '',
    date: '',
    feedbackType: 'positive',
    subject: '',
    message: '',
    priority: 'medium',
    suggestions: '',
    followUpRequired: false,
    followUpDate: ''
  });

  // Sample data - replace with API calls
  const lecturers = [
    { id: 1, name: 'David', courses: ['DIT', 'COMMUNICATION'], email: 'david@luct.ac.za' },
    { id: 2, name: 'Lekhetho', courses: ['DIT', 'fss'], email: 'lekhetho@luct.ac.za' },
    { id: 3, name: 'Tshepo', courses: ['DIT'], email: 'tshepo@luct.ac.za' },
    { id: 4, name: 'Ten', courses: ['DIT'], email: 'ten@luct.ac.za' }
  ];

  const students = [
    { id: 101, name: 'John Doe', class: 'DPRL', course: 'COMMUNICATION', studentId: 'BCOM001' },
    { id: 102, name: 'Jane Smith', class: 'BSCIT', course: 'DIT', studentId: 'DIT001' },
    { id: 103, name: 'Mike Johnson', class: 'DSM', course: 'fss', studentId: 'FSS001' }
  ];

  const courses = ['DIT', 'COMMUNICATION', 'fss', 'BCOM12', 'IT901'];

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFeedbackData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleRecipientTypeChange = (type) => {
    setFeedbackType(type);
    setFeedbackData(prev => ({
      ...prev,
      recipientType: type,
      recipientId: '',
      recipientName: ''
    }));
    setSelectedRecipient('');
  };

  const handleRecipientSelect = (recipient) => {
    setSelectedRecipient(recipient.id);
    setFeedbackData(prev => ({
      ...prev,
      recipientId: recipient.id,
      recipientName: recipient.name,
      course: recipient.courses?.[0] || recipient.course || ''
    }));
  };

  const handleSubmitFeedback = async (e) => {
    e.preventDefault();
    
    if (!feedbackData.recipientId || !feedbackData.message.trim()) {
      alert('Please select a recipient and provide feedback message');
      return;
    }

    try {
      // Simulate API call
      console.log('Submitting feedback:', feedbackData);
      
      // Here you would make actual API call
      // await API.post('/api/feedback', feedbackData);
      
      alert(`‚úÖ Feedback sent successfully to ${feedbackData.recipientName}!`);
      
      // Reset form
      setFeedbackData({
        recipientType: 'lecturer',
        recipientId: '',
        recipientName: '',
        course: '',
        date: new Date().toISOString().split('T')[0],
        feedbackType: 'positive',
        subject: '',
        message: '',
        priority: 'medium',
        suggestions: '',
        followUpRequired: false,
        followUpDate: ''
      });
      setSelectedRecipient('');
      setShowFeedbackModal(false);
      
    } catch (error) {
      alert('Error sending feedback: ' + error.message);
    }
  };

  const getRecipients = () => {
    return feedbackType === 'lecturer' ? lecturers : students;
  };

  const getFeedbackTypeColor = (type) => {
    switch(type) {
      case 'positive': return 'success';
      case 'constructive': return 'warning';
      case 'critical': return 'danger';
      default: return 'primary';
    }
  };

  return (
    <div className="feedback-component">
      {/* Feedback Trigger Button */}
      <div className="text-center mb-4">
        <button 
          className="btn btn-primary btn-lg"
          onClick={() => setShowFeedbackModal(true)}
        >
          <i className="fas fa-comment-medical me-2"></i>
          Send Feedback
        </button>
        <p className="text-muted mt-2">
          Provide constructive feedback to lecturers or students
        </p>
      </div>

      {/* Recent Feedback History (Optional) */}
      <div className="card mb-4">
        <div className="card-header">
          <h6 className="mb-0">
            <i className="fas fa-history me-2"></i>
            Recent Feedback Sent
          </h6>
        </div>
        <div className="card-body">
          <p className="text-muted">No recent feedback. Send your first feedback above.</p>
        </div>
      </div>

      {/* Feedback Modal */}
      {showFeedbackModal && (
        <div className="modal-overlay">
          <div className="modal-content feedback-modal">
            <div className="modal-header bg-primary text-white">
              <h5 className="modal-title">
                <i className="fas fa-comment-dots me-2"></i>
                Send Feedback - Principal Lecturer
              </h5>
              <button 
                type="button" 
                className="btn-close btn-close-white" 
                onClick={() => setShowFeedbackModal(false)}
              ></button>
            </div>

            <form onSubmit={handleSubmitFeedback}>
              <div className="modal-body">
                {/* Recipient Type Selection */}
                <div className="row mb-4">
                  <div className="col-12">
                    <label className="form-label fw-bold">Feedback For:</label>
                    <div className="btn-group w-100" role="group">
                      <button
                        type="button"
                        className={`btn ${feedbackType === 'lecturer' ? 'btn-primary' : 'btn-outline-primary'}`}
                        onClick={() => handleRecipientTypeChange('lecturer')}
                      >
                        <i className="fas fa-user-tie me-2"></i>
                        Lecturer
                      </button>
                      <button
                        type="button"
                        className={`btn ${feedbackType === 'student' ? 'btn-primary' : 'btn-outline-primary'}`}
                        onClick={() => handleRecipientTypeChange('student')}
                      >
                        <i className="fas fa-user-graduate me-2"></i>
                        Student
                      </button>
                    </div>
                  </div>
                </div>

                {/* Recipient Selection */}
                <div className="row mb-3">
                  <div className="col-12">
                    <label className="form-label fw-bold">
                      Select {feedbackType === 'lecturer' ? 'Lecturer' : 'Student'}:
                    </label>
                    <div className="recipient-list">
                      {getRecipients().map(recipient => (
                        <div
                          key={recipient.id}
                          className={`recipient-item ${selectedRecipient === recipient.id ? 'selected' : ''}`}
                          onClick={() => handleRecipientSelect(recipient)}
                        >
                          <div className="recipient-info">
                            <strong>{recipient.name}</strong>
                            {feedbackType === 'lecturer' ? (
                              <small className="text-muted">
                                Courses: {recipient.courses.join(', ')}
                                {recipient.email && ` ‚Ä¢ ${recipient.email}`}
                              </small>
                            ) : (
                              <small className="text-muted">
                                {recipient.class} ‚Ä¢ {recipient.course} ‚Ä¢ ID: {recipient.studentId}
                              </small>
                            )}
                          </div>
                          <i className="fas fa-check select-indicator"></i>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>

                {/* Feedback Details */}
                {selectedRecipient && (
                  <>
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label className="form-label">Course</label>
                        <select
                          className="form-control"
                          name="course"
                          value={feedbackData.course}
                          onChange={handleInputChange}
                        >
                          <option value="">Select Course</option>
                          {courses.map(course => (
                            <option key={course} value={course}>{course}</option>
                          ))}
                        </select>
                      </div>
                      <div className="col-md-6 mb-3">
                        <label className="form-label">Date</label>
                        <input
                          type="date"
                          className="form-control"
                          name="date"
                          value={feedbackData.date || new Date().toISOString().split('T')[0]}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>

                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label className="form-label">Feedback Type</label>
                        <select
                          className={`form-control border-${getFeedbackTypeColor(feedbackData.feedbackType)}`}
                          name="feedbackType"
                          value={feedbackData.feedbackType}
                          onChange={handleInputChange}
                        >
                          <option value="positive">üëç Positive Feedback</option>
                          <option value="constructive">üí° Constructive Feedback</option>
                          <option value="critical">‚ö†Ô∏è Critical Feedback</option>
                        </select>
                      </div>
                      <div className="col-md-6 mb-3">
                        <label className="form-label">Priority</label>
                        <select
                          className="form-control"
                          name="priority"
                          value={feedbackData.priority}
                          onChange={handleInputChange}
                        >
                          <option value="low">Low Priority</option>
                          <option value="medium">Medium Priority</option>
                          <option value="high">High Priority</option>
                        </select>
                      </div>
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Subject</label>
                      <input
                        type="text"
                        className="form-control"
                        name="subject"
                        value={feedbackData.subject}
                        onChange={handleInputChange}
                        placeholder="Brief subject line for the feedback"
                      />
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Feedback Message *</label>
                      <textarea
                        className="form-control"
                        name="message"
                        rows="6"
                        value={feedbackData.message}
                        onChange={handleInputChange}
                        placeholder={`Provide detailed feedback for ${feedbackData.recipientName}...`}
                        required
                      />
                      <div className="form-text">
                        Be specific, constructive, and professional in your feedback.
                      </div>
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Suggestions for Improvement</label>
                      <textarea
                        className="form-control"
                        name="suggestions"
                        rows="3"
                        value={feedbackData.suggestions}
                        onChange={handleInputChange}
                        placeholder="Provide actionable suggestions for improvement..."
                      />
                    </div>

                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            name="followUpRequired"
                            checked={feedbackData.followUpRequired}
                            onChange={handleInputChange}
                          />
                          <label className="form-check-label">
                            Follow-up Required
                          </label>
                        </div>
                      </div>
                      {feedbackData.followUpRequired && (
                        <div className="col-md-6 mb-3">
                          <label className="form-label">Follow-up Date</label>
                          <input
                            type="date"
                            className="form-control"
                            name="followUpDate"
                            value={feedbackData.followUpDate}
                            onChange={handleInputChange}
                          />
                        </div>
                      )}
                    </div>
                  </>
                )}
              </div>

              <div className="modal-footer">
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => setShowFeedbackModal(false)}
                >
                  <i className="fas fa-times me-1"></i>Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-success" 
                  disabled={!selectedRecipient || !feedbackData.message.trim()}
                >
                  <i className="fas fa-paper-plane me-1"></i>
                  Send Feedback
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FeedbackComponent;
