import React, { useState } from 'react';
import './Reports.css';

const ReportsSection = ({ 
  reports, 
  loading, 
  user, 
  setShowAddForm, 
  fetchReports, 
  downloadExcel, 
  handleEditReport, 
  handleDeleteReport, 
  canCreateReport, 
  canViewAllReports, 
  canEditDelete 
}) => {
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');

  const handleAddFeedback = (report) => {
    setSelectedReport(report);
    setFeedback(report.feedback || '');
    setShowFeedbackModal(true);
  };

  const handleSubmitFeedback = () => {
    if (selectedReport && feedback.trim()) {
      // Here you would typically make an API call to save the feedback
      console.log('Submitting feedback for report:', selectedReport.id, feedback);

      // In a real app, you would update state via props or context
      // For now, we'll show an alert
      alert(`Feedback submitted for ${selectedReport.coursename} report!\n\nFeedback: ${feedback}`);

      setShowFeedbackModal(false);
      setFeedback('');
      setSelectedReport(null);

      // Refresh the reports to show updated feedback
      fetchReports();
    }
  };

  // Enhanced role-based permissions
  const canAddFeedback = (report) => {
    // PRL can add feedback to any report in their stream
    if (user?.role === 'PRINCIPAL_LECTURER') {
      return true;
    }
    // PL can view reports from PRL (read-only for now, but could add feedback if needed)
    if (user?.role === 'PROGRAM_LEADER') {
      return true;
    }
    return false;
  };

  const canViewFeedback = () => {
    return user?.role === 'PRINCIPAL_LECTURER' || user?.role === 'PROGRAM_LEADER';
  };

  // Filter reports based on user role
  const getFilteredReports = () => {
    if (!user || !reports) return reports;

    switch(user.role) {
      case 'PRINCIPAL_LECTURER':
        // PRL views all reports under their stream
        // In a real app, you would filter by stream/courses under PRL's responsibility
        return reports.filter(report => 
          report.stream === user.stream || 
          report.courseStream === user.stream ||
          true // Remove this in production - for demo purposes
        );

      case 'PROGRAM_LEADER':
        // PL views reports from PRL
        // In a real app, you would filter by reports that have PRL feedback
        return reports.filter(report => 
          report.hasPRLFeedback || 
          report.reviewedByPRL ||
          true // Remove this in production - for demo purposes
        );

      case 'LECTURER':
        // Lecturers see only their own reports
        return reports.filter(report => 
          report.lecturer === user.name || 
          report.lecturerId === user.id
        );

      case 'STUDENT':
        // Students see reports for their courses/classes
        return reports.filter(report => 
          report.class_name === user.className ||
          report.courseCode === user.courseCode
        );

      default:
        return reports;
    }
  };

  const filteredReports = getFilteredReports();

  const getHeaderTitle = () => {
    switch(user?.role) {
      case 'STUDENT':
        return 'Available Reports';
      case 'LECTURER':
        return 'My Class Reports';
      case 'PRINCIPAL_LECTURER':
        return 'Stream Reports - Add Feedback';
      case 'PROGRAM_LEADER':
        return 'PRL Reports Review';
      default:
        return 'All Class Reports';
    }
  };

  return (
    <>
      <div className="card p-3">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0">
            {getHeaderTitle()}
          </h5>
          <div>
            {canCreateReport && (
              <button 
                className="btn btn-success btn-sm me-2"
                onClick={() => setShowAddForm(true)}
              >
                + Add New Report
              </button>
            )}
            {canViewAllReports && (
              <button 
                className="btn btn-excel btn-sm me-2"
                onClick={downloadExcel}
                disabled={filteredReports.length === 0}
              >
                <i className="fas fa-file-excel me-1"></i>Export All
              </button>
            )}
            <button 
              className="btn btn-primary btn-sm"
              onClick={fetchReports}
              disabled={loading}
            >
              {loading ? 'Refreshing...' : 'Refresh'}
            </button>
          </div>
        </div>

        {/* Role-based information banners */}
        {user?.role === 'PRINCIPAL_LECTURER' && (
          <div className="alert alert-info mb-3 py-2">
            <small>
              <i className="fas fa-info-circle me-2"></i>
              You can view all reports in your stream and provide feedback to lecturers.
            </small>
          </div>
        )}

        {user?.role === 'PROGRAM_LEADER' && (
          <div className="alert alert-warning mb-3 py-2">
            <small>
              <i className="fas fa-eye me-2"></i>
              Viewing reports with PRL feedback. Monitor teaching quality across programs.
            </small>
          </div>
        )}

        <div className="table-responsive">
          <table className="table table-dark table-striped">
            <thead>
              <tr>
                <th>#</th>
                <th>Course</th>
                <th>Code</th>
                <th>Lecturer</th>
                <th>Class</th>
                <th>Attendance</th>
                <th>Week</th>
                <th>Date</th>
                {canViewFeedback() && <th>Feedback Status</th>}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan={canViewFeedback() ? "10" : "9"} className="text-center">
                    <div className="spinner-border spinner-border-sm me-2"></div>
                    Loading reports...
                  </td>
                </tr>
              ) : filteredReports.length === 0 ? (
                <tr>
                  <td colSpan={canViewFeedback() ? "10" : "9"} className="text-center text-muted py-4">
                    <div>No reports found.</div>
                    <small>
                      {user?.role === 'STUDENT' 
                        ? 'No reports available for your courses.' 
                        : user?.role === 'PRINCIPAL_LECTURER'
                        ? 'No reports available in your stream.'
                        : user?.role === 'PROGRAM_LEADER'
                        ? 'No reports with PRL feedback available.'
                        : 'Click "Add New Report" to create your first report'
                      }
                    </small>
                  </td>
                </tr>
              ) : (
                filteredReports.map((report, index) => (
                  <tr key={report.id}>
                    <td>{index + 1}</td>
                    <td>{report.coursename || 'N/A'}</td>
                    <td>{report.coursecode || 'N/A'}</td>
                    <td>{report.lecturer || 'N/A'}</td>
                    <td>{report.class_name || 'N/A'}</td>
                    <td>
                      <span className="badge bg-info">
                        {report.present || 0}/{report.registered || 0}
                      </span>
                    </td>
                    <td>{report.week_of_reporting || 'N/A'}</td>
                    <td>
                      {report.date_of_lecture ? 
                        new Date(report.date_of_lecture).toLocaleDateString() : 'N/A'
                      }
                    </td>
                    {canViewFeedback() && (
                      <td>
                        {report.feedback ? (
                          <span className="badge bg-success" title={report.feedback}>
                            <i className="fas fa-check me-1"></i>Feedback Provided
                          </span>
                        ) : user?.role === 'PRINCIPAL_LECTURER' ? (
                          <span className="badge bg-warning">Awaiting Feedback</span>
                        ) : (
                          <span className="badge bg-secondary">No Feedback</span>
                        )}
                      </td>
                    )}
                    <td>
                      <div className="btn-group" role="group">
                        {canEditDelete(report) && (
                          <>
                            <button 
                              className="btn btn-warning btn-sm me-1"
                              onClick={() => handleEditReport(report)}
                              title="Edit Report"
                            >
                              <i className="fas fa-edit"></i>
                            </button>
                            <button 
                              className="btn btn-danger btn-sm me-1"
                              onClick={() => handleDeleteReport(report.id, report)}
                              title="Delete Report"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </>
                        )}

                        {/* Feedback Button - Only for PRL and PL */}
                        {canAddFeedback(report) && (
                          <button 
                            className={`btn btn-sm me-1 ${
                              report.feedback ? 'btn-outline-info' : 'btn-info'
                            }`}
                            onClick={() => handleAddFeedback(report)}
                            title={report.feedback ? "Edit Feedback" : "Add Feedback"}
                          >
                            <i className="fas fa-comment-dots"></i>
                            {report.feedback && <i className="fas fa-check ms-1"></i>}
                          </button>
                        )}

                        <button 
                          className="btn btn-primary btn-sm"
                          onClick={() => {
                            const details = `
Report Details:
• Course: ${report.coursename}
• Code: ${report.coursecode}
• Lecturer: ${report.lecturer}
• Class: ${report.class_name}
• Attendance: ${report.present || 0}/${report.registered || 0}
• Week: ${report.week_of_reporting}
• Date: ${report.date_of_lecture ? new Date(report.date_of_lecture).toLocaleDateString() : 'N/A'}
• Topic: ${report.topic_taught || 'N/A'}
${report.feedback ? `• PRL Feedback: ${report.feedback}` : '• Feedback: Not provided yet'}
                            `;
                            alert(details);
                          }}
                          title="View Details"
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Feedback Modal */}
      {showFeedbackModal && (
        <div className="modal fade show" style={{display: 'block', backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">
                  <i className="fas fa-comment-dots me-2"></i>
                  {user?.role === 'PRINCIPAL_LECTURER' ? 'Provide Teaching Feedback' : 'Review Report Feedback'}
                </h5>
                <button 
                  type="button" 
                  className="btn-close btn-close-white" 
                  onClick={() => setShowFeedbackModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                {selectedReport && (
                  <div className="row mb-4">
                    <div className="col-md-6">
                      <h6>Report Information</h6>
                      <table className="table table-sm table-bordered">
                        <tbody>
                          <tr>
                            <td><strong>Course:</strong></td>
                            <td>{selectedReport.coursename}</td>
                          </tr>
                          <tr>
                            <td><strong>Code:</strong></td>
                            <td>{selectedReport.coursecode}</td>
                          </tr>
                          <tr>
                            <td><strong>Lecturer:</strong></td>
                            <td>{selectedReport.lecturer}</td>
                          </tr>
                          <tr>
                            <td><strong>Class:</strong></td>
                            <td>{selectedReport.class_name}</td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                    <div className="col-md-6">
                      <h6>Class Details</h6>
                      <table className="table table-sm table-bordered">
                        <tbody>
                          <tr>
                            <td><strong>Attendance:</strong></td>
                            <td>
                              <span className="badge bg-info">
                                {selectedReport.present || 0}/{selectedReport.registered || 0}
                              </span>
                            </td>
                          </tr>
                          <tr>
                            <td><strong>Week:</strong></td>
                            <td>{selectedReport.week_of_reporting}</td>
                          </tr>
                          <tr>
                            <td><strong>Date:</strong></td>
                            <td>
                              {selectedReport.date_of_lecture ? 
                                new Date(selectedReport.date_of_lecture).toLocaleDateString() : 'N/A'
                              }
                            </td>
                          </tr>
                          <tr>
                            <td><strong>Topic:</strong></td>
                            <td>{selectedReport.topic_taught || 'N/A'}</td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                  </div>
                )}
                <div className="mb-3">
                  <label htmlFor="feedbackText" className="form-label fw-bold">
                    {user?.role === 'PRINCIPAL_LECTURER' ? 'Your Teaching Feedback:' : 'PRL Feedback Review:'}
                  </label>
                  <textarea 
                    className="form-control" 
                    id="feedbackText" 
                    rows="6"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    placeholder={
                      user?.role === 'PRINCIPAL_LECTURER' 
                        ? "Provide constructive feedback on teaching methods, student engagement, areas for improvement, etc."
                        : "Add any additional comments or review notes for this report..."
                    }
                    style={{ resize: 'vertical' }}
                  ></textarea>
                  <div className="form-text">
                    {user?.role === 'PRINCIPAL_LECTURER'
                      ? "Your feedback will help improve teaching quality in your stream."
                      : "Monitor teaching quality and provide program-level insights."
                    }
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => setShowFeedbackModal(false)}
                >
                  <i className="fas fa-times me-1"></i>Cancel
                </button>
                <button 
                  type="button" 
                  className="btn btn-success" 
                  onClick={handleSubmitFeedback}
                  disabled={!feedback.trim()}
                >
                  <i className="fas fa-check me-1"></i>
                  {user?.role === 'PRINCIPAL_LECTURER' ? 'Submit Feedback' : 'Save Review'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ReportsSection;
