import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Form, Button, Table, Badge, Alert } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './Rating.css';

const RatingSection = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [students, setStudents] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({
    targetType: '',
    targetId: '',
    courseId: '',
    rating: '',
    comments: '',
    criteria: {
      punctuality: 0,
      preparation: 0,
      engagement: 0,
      knowledge: 0,
      communication: 0
    }
  });

  useEffect(() => {
    loadRatings();
    loadData();
  }, [user]);

  const loadRatings = async () => {
    try {
      const response = await axios.get('/api/ratings');
      setRatings(response.data);
    } catch (error) {
      console.error('Error loading ratings:', error);
    }
  };

  const loadData = async () => {
    try {
      // Load students for lecturers to rate
      if (user?.role === 'lecturer') {
        const studentsResponse = await axios.get('/api/students');
        setStudents(studentsResponse.data);
      }

      // Load lecturers for PRL/PL to rate
      if (user?.role === 'principal_lecturer' || user?.role === 'program_leader') {
        const lecturersResponse = await axios.get('/api/lecturers');
        setLecturers(lecturersResponse.data);
      }

      // Load courses
      const coursesResponse = await axios.get('/api/courses');
      setCourses(coursesResponse.data);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/ratings', {
        ...formData,
        ratedBy: user.id,
        ratedByName: user.name
      });

      alert('Rating submitted successfully!');
      setFormData({
        targetType: '',
        targetId: '',
        courseId: '',
        rating: '',
        comments: '',
        criteria: {
          punctuality: 0,
          preparation: 0,
          engagement: 0,
          knowledge: 0,
          communication: 0
        }
      });
      loadRatings();
    } catch (error) {
      console.error('Error submitting rating:', error);
      alert('Error submitting rating');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCriteriaChange = (criteria, value) => {
    setFormData(prev => ({
      ...prev,
      criteria: {
        ...prev.criteria,
        [criteria]: parseInt(value)
      }
    }));
  };

  const calculateOverallRating = (criteria) => {
    const values = Object.values(criteria);
    return (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1);
  };

  const getRatingColor = (rating) => {
    if (rating >= 4) return 'success';
    if (rating >= 3) return 'warning';
    return 'danger';
  };

  const getTargetOptions = () => {
    switch (user?.role) {
      case 'lecturer':
        return students.map(student => ({
          value: student.id,
          label: `${student.name} (${student.studentId})`
        }));
      case 'principal_lecturer':
      case 'program_leader':
        return lecturers.map(lecturer => ({
          value: lecturer.id,
          label: `${lecturer.name} - ${lecturer.department}`
        }));
      default:
        return [];
    }
  };

  const canRate = user?.role === 'lecturer' || user?.role === 'principal_lecturer' || user?.role === 'program_leader';

  if (!canRate) {
    return (
      <Container>
        <Alert variant="info">
          <h4>Rating Access</h4>
          <p>As a student, you can view ratings but cannot submit them. Lecturers and administrators can submit ratings.</p>
        </Alert>
      </Container>
    );
  }

  return (
    <Container fluid>
      <Row>
        <Col lg={6}>
          <Card className="mb-4">
            <Card.Header>
              <h5 className="mb-0">
                <i className="fas fa-star me-2"></i>
                {user?.role === 'lecturer' ? 'Rate Students' : 'Rate Lecturers'}
              </h5>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleSubmit}>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Course</Form.Label>
                      <Form.Select
                        name="courseId"
                        value={formData.courseId}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select Course</option>
                        {courses.map(course => (
                          <option key={course.id} value={course.id}>
                            {course.course_code} - {course.course_name}
                          </option>
                        ))}
                      </Form.Select>
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>
                        {user?.role === 'lecturer' ? 'Select Student' : 'Select Lecturer'}
                      </Form.Label>
                      <Form.Select
                        name="targetId"
                        value={formData.targetId}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select {user?.role === 'lecturer' ? 'Student' : 'Lecturer'}</option>
                        {getTargetOptions().map(option => (
                          <option key={option.value} value={option.value}>
                            {option.label}
                          </option>
                        ))}
                      </Form.Select>
                    </Form.Group>
                  </Col>
                </Row>

                <h6 className="mt-4 mb-3">Rating Criteria (1-5)</h6>

                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Punctuality</Form.Label>
                      <Form.Select
                        value={formData.criteria.punctuality}
                        onChange={(e) => handleCriteriaChange('punctuality', e.target.value)}
                        required
                      >
                        <option value={0}>Select Rating</option>
                        {[1, 2, 3, 4, 5].map(num => (
                          <option key={num} value={num}>{num} - {num === 1 ? 'Poor' : num === 5 ? 'Excellent' : 'Average'}</option>
                        ))}
                      </Form.Select>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Preparation</Form.Label>
                      <Form.Select
                        value={formData.criteria.preparation}
                        onChange={(e) => handleCriteriaChange('preparation', e.target.value)}
                        required
                      >
                        <option value={0}>Select Rating</option>
                        {[1, 2, 3, 4, 5].map(num => (
                          <option key={num} value={num}>{num} - {num === 1 ? 'Poor' : num === 5 ? 'Excellent' : 'Average'}</option>
                        ))}
                      </Form.Select>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Engagement</Form.Label>
                      <Form.Select
                        value={formData.criteria.engagement}
                        onChange={(e) => handleCriteriaChange('engagement', e.target.value)}
                        required
                      >
                        <option value={0}>Select Rating</option>
                        {[1, 2, 3, 4, 5].map(num => (
                          <option key={num} value={num}>{num} - {num === 1 ? 'Poor' : num === 5 ? 'Excellent' : 'Average'}</option>
                        ))}
                      </Form.Select>
                    </Form.Group>
                  </Col>

                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Knowledge</Form.Label>
                      <Form.Select
                        value={formData.criteria.knowledge}
                        onChange={(e) => handleCriteriaChange('knowledge', e.target.value)}
                        required
                      >
                        <option value={0}>Select Rating</option>
                        {[1, 2, 3, 4, 5].map(num => (
                          <option key={num} value={num}>{num} - {num === 1 ? 'Poor' : num === 5 ? 'Excellent' : 'Average'}</option>
                        ))}
                      </Form.Select>
                    </Form.Group>

                    <Form.Group className="mb-3">
                      <Form.Label>Communication</Form.Label>
                      <Form.Select
                        value={formData.criteria.communication}
                        onChange={(e) => handleCriteriaChange('communication', e.target.value)}
                        required
                      >
                        <option value={0}>Select Rating</option>
                        {[1, 2, 3, 4, 5].map(num => (
                          <option key={num} value={num}>{num} - {num === 1 ? 'Poor' : num === 5 ? 'Excellent' : 'Average'}</option>
                        ))}
                      </Form.Select>
                    </Form.Group>

                    <div className="mb-3 p-3 bg-light rounded">
                      <strong>Overall Rating: </strong>
                      <Badge bg={getRatingColor(calculateOverallRating(formData.criteria))}>
                        {calculateOverallRating(formData.criteria)} / 5
                      </Badge>
                    </div>
                  </Col>
                </Row>

                <Form.Group className="mb-3">
                  <Form.Label>Additional Comments</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name="comments"
                    value={formData.comments}
                    onChange={handleInputChange}
                    placeholder="Enter any additional comments or feedback..."
                  />
                </Form.Group>

                <Button variant="primary" type="submit" disabled={!formData.courseId || !formData.targetId}>
                  Submit Rating
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>

        <Col lg={6}>
          <Card>
            <Card.Header>
              <h5 className="mb-0">Rating History</h5>
            </Card.Header>
            <Card.Body>
              <div className="table-responsive">
                <Table striped hover>
                  <thead>
                    <tr>
                      <th>Target</th>
                      <th>Course</th>
                      <th>Rating</th>
                      <th>Date</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {ratings.map(rating => (
                      <tr key={rating.id}>
                        <td>{rating.targetName}</td>
                        <td>{rating.courseName}</td>
                        <td>
                          <Badge bg={getRatingColor(rating.overallRating)}>
                            {rating.overallRating} â˜…
                          </Badge>
                        </td>
                        <td>{new Date(rating.createdAt).toLocaleDateString()}</td>
                        <td>
                          <Button variant="outline-primary" size="sm">
                            View Details
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default RatingSection;
