import React, { useState } from 'react';
import './Lecture.css';

const LecturersSection = () => {
  const [lecturers, setLecturers] = useState([
    { 
      id: 1, 
      name: 'Tsepo', 
      email: 'tsepo@luct.ac.ls', 
      courses: ['DIT', 'Web Development'], 
      status: 'Active',
      ratings: {
        pl: { average: 4.2, count: 5 },
        prl: { average: 4.0, count: 3 },
        student: { average: 4.5, count: 20 },
        lecturer: { average: 4.1, count: 8 }
      }
    },
    { 
      id: 2, 
      name: 'Lereko', 
      email: 'lereko@luct.ac.ls', 
      courses: ['CS101'], 
      status: 'Active',
      ratings: {
        pl: { average: 3.8, count: 2 },
        prl: { average: 4.2, count: 4 },
        student: { average: 3.9, count: 15 },
        lecturer: { average: 4.0, count: 6 }
      }
    },
    { 
      id: 3, 
      name: 'Teboho', 
      email: 'teboho@luct.ac.ls', 
      courses: ['IT901'], 
      status: 'Active',
      ratings: {
        pl: { average: 4.5, count: 4 },
        prl: { average: 4.3, count: 3 },
        student: { average: 4.7, count: 25 },
        lecturer: { average: 4.4, count: 10 }
      }
    }
  ]);

  const [showRatingModal, setShowRatingModal] = useState(false);
  const [selectedLecturer, setSelectedLecturer] = useState(null);
  const [newRating, setNewRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [userType, setUserType] = useState('student');
  const [currentUser, setCurrentUser] = useState({ type: 'student', name: 'Current User' });

  const userTypes = [
    { value: 'pl', label: 'Program Leader (PL)', icon: 'fas fa-user-tie' },
    { value: 'prl', label: 'Program Representative (PRL)', icon: 'fas fa-user-check' },
    { value: 'student', label: 'Student', icon: 'fas fa-user-graduate' },
    { value: 'lecturer', label: 'Lecturer', icon: 'fas fa-chalkboard-teacher' }
  ];

  const handleRateLecturer = (lecturer) => {
    setSelectedLecturer(lecturer);
    setNewRating(0);
    setShowRatingModal(true);
  };

  const handleSubmitRating = () => {
    if (newRating === 0) {
      alert('Please select a rating before submitting.');
      return;
    }

    const updatedLecturers = lecturers.map(lecturer => {
      if (lecturer.id === selectedLecturer.id) {
        const currentUserType = userType;
        const currentRatings = lecturer.ratings[currentUserType];
        const newCount = currentRatings.count + 1;
        const newAverage = (
          (currentRatings.average * currentRatings.count + newRating) / newCount
        ).toFixed(1);

        return {
          ...lecturer,
          ratings: {
            ...lecturer.ratings,
            [currentUserType]: {
              average: parseFloat(newAverage),
              count: newCount
            }
          }
        };
      }
      return lecturer;
    });

    setLecturers(updatedLecturers);
    setShowRatingModal(false);
    setSelectedLecturer(null);
    setNewRating(0);

    // Show success message
    alert(`Thank you for rating ${selectedLecturer.name} as a ${userTypes.find(u => u.value === userType)?.label}!`);
  };

  const calculateOverallRating = (ratings) => {
    const totalRatings = Object.values(ratings).reduce((sum, rating) => sum + rating.count, 0);
    if (totalRatings === 0) return 0;

    const weightedSum = Object.values(ratings).reduce((sum, rating) => 
      sum + (rating.average * rating.count), 0
    );

    return (weightedSum / totalRatings).toFixed(1);
  };

  const renderStars = (rating, interactive = false, onStarClick = null, onStarHover = null) => {
    return (
      <div className="stars">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            className={`star ${interactive ? 'interactive' : ''} ${
              star <= (interactive ? (hoverRating || newRating) : rating) ? 'filled' : ''
            }`}
            onClick={() => interactive && onStarClick && onStarClick(star)}
            onMouseEnter={() => interactive && onStarHover && onStarHover(star)}
            onMouseLeave={() => interactive && onStarHover && onStarHover(0)}
          >
            ★
          </span>
        ))}
        {!interactive && rating > 0 && (
          <span className="rating-text ms-2">
            {rating}
          </span>
        )}
      </div>
    );
  };

  const renderRatingBreakdown = (ratings) => {
    return (
      <div className="rating-breakdown">
        {userTypes.map(userType => (
          <div key={userType.value} className="rating-category">
            <div className="d-flex justify-content-between align-items-center mb-1">
              <small className="text-muted">
                <i className={`${userType.icon} me-1`}></i>
                {userType.label}:
              </small>
              <small className="fw-bold">
                {ratings[userType.value].average > 0 ? (
                  <>
                    {ratings[userType.value].average} ★ 
                    <span className="text-muted ms-1">({ratings[userType.value].count})</span>
                  </>
                ) : (
                  <span className="text-muted">No ratings</span>
                )}
              </small>
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="card">
      <div className="card-header d-flex justify-content-between align-items-center">
        <h5 className="mb-0"><i className="fas fa-chalkboard-teacher me-2"></i>Lecturers Management</h5>
        <div className="d-flex align-items-center">
          <div className="me-3">
            <small className="text-muted me-2">Logged in as:</small>
            <select 
              className="form-select form-select-sm" 
              value={userType}
              onChange={(e) => setUserType(e.target.value)}
              style={{width: 'auto'}}
            >
              {userTypes.map(type => (
                <option key={type.value} value={type.value}>
                  {type.label}
                </option>
              ))}
            </select>
          </div>
          <button className="btn btn-success btn-sm">
            <i className="fas fa-plus me-1"></i>Add Lecturer
          </button>
        </div>
      </div>
      <div className="card-body">
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Courses</th>
                <th>Overall Rating</th>
                <th>Rating Breakdown</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {lecturers.map(lecturer => {
                const overallRating = calculateOverallRating(lecturer.ratings);
                return (
                  <tr key={lecturer.id}>
                    <td>{lecturer.name}</td>
                    <td>{lecturer.email}</td>
                    <td>{lecturer.courses.join(', ')}</td>
                    <td>
                      <div className="d-flex align-items-center">
                        {overallRating > 0 ? (
                          renderStars(overallRating)
                        ) : (
                          <span className="text-muted">No ratings</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <button 
                        className="btn btn-outline-info btn-sm"
                        data-bs-toggle="tooltip"
                        title="View rating breakdown"
                        onClick={() => {
                          // You can implement a detailed breakdown modal here
                          alert(`Rating breakdown for ${lecturer.name}:\n\n` +
                            `PL: ${lecturer.ratings.pl.average} ★ (${lecturer.ratings.pl.count} ratings)\n` +
                            `PRL: ${lecturer.ratings.prl.average} ★ (${lecturer.ratings.prl.count} ratings)\n` +
                            `Students: ${lecturer.ratings.student.average} ★ (${lecturer.ratings.student.count} ratings)\n` +
                            `Lecturers: ${lecturer.ratings.lecturer.average} ★ (${lecturer.ratings.lecturer.count} ratings)`);
                        }}
                      >
                        <i className="fas fa-chart-bar"></i> Details
                      </button>
                    </td>
                    <td>
                      <span className={`badge ${lecturer.status === 'Active' ? 'bg-success' : 'bg-warning'}`}>
                        {lecturer.status}
                      </span>
                    </td>
                    <td>
                      <button 
                        className="btn btn-primary btn-sm me-1"
                        onClick={() => handleRateLecturer(lecturer)}
                        title={`Rate as ${userTypes.find(u => u.value === userType)?.label}`}
                      >
                        <i className="fas fa-star"></i> Rate
                      </button>
                      <button className="btn btn-warning btn-sm me-1">
                        <i className="fas fa-edit"></i>
                      </button>
                      <button className="btn btn-info btn-sm me-1">
                        <i className="fas fa-eye"></i>
                      </button>
                      <button className="btn btn-danger btn-sm">
                        <i className="fas fa-trash"></i>
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* Rating Modal */}
      {showRatingModal && selectedLecturer && (
        <div className="modal show d-block" tabIndex="-1" style={{backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Rate Lecturer</h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setShowRatingModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="text-center mb-4">
                  <h6>Rate {selectedLecturer.name}</h6>
                  <p className="text-muted">
                    You are rating as: <strong>{userTypes.find(u => u.value === userType)?.label}</strong>
                  </p>

                  <div className="current-ratings mb-3 p-3 bg-light rounded">
                    <h6>Current Ratings:</h6>
                    {renderRatingBreakdown(selectedLecturer.ratings)}
                  </div>

                  <div className="rating-section">
                    <h6>Your Rating:</h6>
                    <div className="rating-stars mb-3">
                      {renderStars(
                        selectedLecturer.ratings[userType].average, 
                        true, 
                        setNewRating, 
                        setHoverRating
                      )}
                    </div>

                    <p className="text-muted">
                      {newRating === 0 ? 'Select a rating (1-5 stars)' : `You selected: ${newRating} stars`}
                    </p>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => setShowRatingModal(false)}
                >
                  Cancel
                </button>
                <button 
                  type="button" 
                  className="btn btn-primary" 
                  onClick={handleSubmitRating}
                >
                  Submit Rating as {userTypes.find(u => u.value === userType)?.label}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LecturersSection;
